import styled from "styled-components";
import Button from "./Button";

const Item = styled.div`
    margin-top: 1%;
    border: 1px solid;
    border-radius: 10px;
    padding: 1%;
    width: 55%;
    height: 3em;
    text-align: left;
`
const Text = styled.p`
    font-size: 2em;
    display: inline;
`

const ButtonDiv = styled.div`
    float: right;
    display: inline;
`

function TodoItem(props) {

    function handleClick(){
        if(text !== ""){
            props.onSubmit(text)
            setText("")
        }
    }
    return (
        <Item>
            <Text>
                {props.text}
            </Text>
            <ButtonDiv>
                <Button
                    text="Edit"
                    color="#00b8a9"
                />
                <Button
                    text="Delete"
                    color="#f6416c"
                />
            </ButtonDiv>
        </Item>
    );
}

export default TodoItem;
