import styled from "styled-components";

const ButtonStyled = styled.button`
    font-size: 1.5em;
    text-align: center;
    background-color: ${props => props.color ? props.color : "white"};
    color: white;
    border: none;
    border-radius: 5px;
    padding: 10px 10px;
    width: 100px;
    float: ${props => props.isFloatRight ? "right" : "" };
`

function Button(props) {
    return (
            <ButtonStyled
                color={props.color}
                onClick={props.onClick}
                float={props.isFloatRight}
            >
                {props.text}
            </ButtonStyled>
    );
}

export default Button;
