import styled from "styled-components";
import Button from "./Button";
import React, { useState } from "react";

const Item = styled.div`
    margin-top: 1%;
    border: 1px solid;
    border-radius: 10px;
    padding: 1%;
    width: 55%;
    height: 3em;
    text-align: left;
`
const Text = styled.p`
    font-size: 2em;
    display: inline;
`

const ButtonDiv = styled.div`
    float: right;
    display: inline;
`

function TodoItem(props) {

    const [isEdited, setIsEdited] = useState(false)

    function handleDelete() {
        props.onDelete(props.index)
    }

    function handleEdit() {
        setIsEdited(true)
    }

    function handleUpdate() {
        setIsEdited(false)
    }

    return (
        <Item>
            {!isEdited
                ?
                <>
                    <Text>
                        {props.text}
                    </Text>
                    <ButtonDiv>

                        <Button
                            text="Edit"
                            color="#99ddcc"
                            onClick={handleEdit}
                        />
                        <Button
                            text="Delete"
                            color="#f6416c"
                            onClick={handleDelete}
                        />
                </ButtonDiv>
                </>
                    :
                    <p>
                        hey
                    </p>
            }
        </Item>
    );
}

{/* <Button
    text="Update"
    color="#00b8a9"
    onClick={handleUpdate}
/> */}

export default TodoItem;
